import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAppContext } from '../App';
import { 
  Shield, 
  Search, 
  User, 
  LogOut, 
  Menu, 
  X, 
  Moon, 
  Sun,
  BarChart3,
  Home,
  FileText
} from 'lucide-react';
import './Header.css';

const Header = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isUserMenuOpen, setIsUserMenuOpen] = useState(false);
  const { user, setUser, theme, setTheme, searchQuery, setSearchQuery } = useAppContext();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    setUser(null);
    navigate('/login');
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      navigate('/results');
    }
  };

  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  const navigationItems = [
    { path: '/dashboard', label: 'Dashboard', icon: Home },
    { path: '/trends', label: 'Trends', icon: BarChart3 },
  ];

  const isActivePath = (path) => {
    return location.pathname === path;
  };

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo and Brand */}
        <div className="header-brand">
          <button 
            className="brand-button"
            onClick={() => navigate('/dashboard')}
          >
            <Shield className="brand-icon" />
            <span className="brand-text">GovPortal</span>
          </button>
        </div>

        {/* Search Bar */}


        {/* Desktop Navigation */}
        <nav className="header-nav desktop-nav">
          {navigationItems.map((item) => {
            const Icon = item.icon;
            return (
              <button
                key={item.path}
                className={`nav-item ${isActivePath(item.path) ? 'active' : ''}`}
                onClick={() => navigate(item.path)}
              >
                <Icon className="nav-icon" />
                <span className="nav-label">{item.label}</span>
              </button>
            );
          })}
        </nav>

        {/* Header Actions */}
        <div className="header-actions">
          {/* Theme Toggle */}
          <button
            className="action-button theme-toggle"
            onClick={toggleTheme}
            aria-label={`Switch to ${theme === 'light' ? 'dark' : 'light'} mode`}
          >
            {theme === 'light' ? <Moon /> : <Sun />}
          </button>

          {/* User Menu */}
          <div className="user-menu-wrapper">
            <button
              className="action-button user-button"
              onClick={() => setIsUserMenuOpen(!isUserMenuOpen)}
              aria-label="User menu"
            >
              <User />
            </button>

            {isUserMenuOpen && (
              <div className="user-menu">
                <div className="user-info">
                  <div className="user-avatar">
                    <User />
                  </div>
                  <div className="user-details">
                    <div className="user-name">{user?.name || 'User'}</div>
                    <div className="user-email">{user?.email}</div>
                  </div>
                </div>
                <div className="user-menu-divider"></div>
                <button
                  className="user-menu-item"
                  onClick={handleLogout}
                >
                  <LogOut className="menu-item-icon" />
                  <span>Sign Out</span>
                </button>
              </div>
            )}
          </div>

          {/* Mobile Menu Toggle */}
          <button
            className="action-button mobile-menu-toggle"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            aria-label="Toggle mobile menu"
          >
            {isMobileMenuOpen ? <X /> : <Menu />}
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMobileMenuOpen && (
        <div className="mobile-nav">
          <div className="mobile-nav-content">
            {navigationItems.map((item) => {
              const Icon = item.icon;
              return (
                <button
                  key={item.path}
                  className={`mobile-nav-item ${isActivePath(item.path) ? 'active' : ''}`}
                  onClick={() => {
                    navigate(item.path);
                    setIsMobileMenuOpen(false);
                  }}
                >
                  <Icon className="mobile-nav-icon" />
                  <span className="mobile-nav-label">{item.label}</span>
                </button>
              );
            })}

            <div className="mobile-nav-divider"></div>

            <div className="mobile-user-info">
              <div className="mobile-user-avatar">
                <User />
              </div>
              <div className="mobile-user-details">
                <div className="mobile-user-name">{user?.name || 'User'}</div>
                <div className="mobile-user-email">{user?.email}</div>
              </div>
            </div>

            <button
              className="mobile-nav-item logout-item"
              onClick={() => {
                handleLogout();
                setIsMobileMenuOpen(false);
              }}
            >
              <LogOut className="mobile-nav-icon" />
              <span className="mobile-nav-label">Sign Out</span>
            </button>
          </div>
        </div>
      )}

      {/* Mobile Menu Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="mobile-nav-overlay"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}

      {/* User Menu Overlay */}
      {isUserMenuOpen && (
        <div 
          className="user-menu-overlay"
          onClick={() => setIsUserMenuOpen(false)}
        />
      )}
    </header>
  );
};

export default Header;
